        public static bool IsForbiddenDomain(string emailAddress)
        {
            if (!string.IsNullOrEmpty(ConfigurationManager.AppSettings["BlacklistedEmailDomains"]))
            {
                string[] emailDomainsForbidden = new List<string>(ConfigurationManager.AppSettings["BlacklistedEmailDomains"].Trim().Split(new char[] { ',' })).ToArray();

                foreach (string domain in emailDomainsForbidden)
                {
                    if (emailAddress.ToLower().Contains(domain))
                    {
                        return true;
                    }
                }
            }

            return false;
        }

        public static string GetTranslationsForPage()
        {
            string[] path = HttpContext.Current.Request.Url.AbsolutePath.ToLower().Split('/').ToArray();

            #region no path -> redirect

            if (path.Length == 0)
            {
                return "";
            }

            #endregion

            if (path.Length > 2)
            {
                if (path[path.Length - 1] == "prm-software")
                {
                    string lang = path[path.Length - 2].ToLower();

                    if (lang != "")
                    {
                        if (ConfigurationManager.AppSettings["TranslationLanguages"] != null)
                        {
                            List<string> defaultTranslations = ConfigurationManager.AppSettings["TranslationLanguages"].ToString().Split(',').ToList();

                            if (defaultTranslations.Count > 0)
                            {
                                foreach (string trans in defaultTranslations)
                                {
                                    if (trans != "" && trans.Contains(lang))
                                    {
                                        return trans;
                                    }
                                }

                                return "";
                            }
                            else
                                return "";
                        }
                        else
                            return "";
                    }
                    else
                        return "";
                }
                else
                    return "";
            }
            else
                return "";
        }

        public static string FixNumberPattern(string inputValue)
        {
            try
            {
                char delimiter = '.';
                string[] partsOfNumber = inputValue.Split(delimiter);

                long leftPart = 0;
                long rightPart = 0;

                if (partsOfNumber[0].Length > 0)
                {
                    if (Validations.IsNumeric(partsOfNumber[0]))
                        leftPart = long.Parse(partsOfNumber[0]);
                }
                else
                    return inputValue;

                if (partsOfNumber.Length > 1)
                {
                    rightPart = long.Parse(partsOfNumber[1]);
                }
                else
                {
                    delimiter = ',';
                    partsOfNumber = inputValue.Split(delimiter);

                    if (partsOfNumber[0].Length > 0)
                    {
                        if (Validations.IsNumeric(partsOfNumber[0]))
                            leftPart = long.Parse(partsOfNumber[0]);
                    }
                    else
                        return inputValue;

                    if (partsOfNumber.Length > 1)
                    {
                        rightPart = long.Parse(partsOfNumber[1]);
                    }
                    else
                        delimiter = '.';
                }

                int numberLength = leftPart.ToString().Length;
                int delimitersCount = numberLength / 3;
                int firstDigitsCount = numberLength % 3;
                string numberDelimiter = delimiter == '.' ? "," : ".";
                string finalNumber = "";

                if (firstDigitsCount > 0)
                {
                    int startIndex = 0;
                    int endIndex = numberLength - (numberLength - firstDigitsCount);
                    finalNumber = leftPart.ToString().Substring(startIndex, endIndex);
                    for (int i = 0; i < delimitersCount; i++)
                    {
                        startIndex = endIndex;
                        endIndex = startIndex + 3;

                        if (endIndex <= numberLength)
                        {
                            finalNumber += numberDelimiter + leftPart.ToString().Substring(startIndex, 3);
                        }
                    }
                }
                else
                {
                    int startIndex = 0;
                    int endIndex = 3;

                    for (int i = 0; i < delimitersCount; i++)
                    {
                        if (i < delimitersCount - 1)
                        {
                            finalNumber += leftPart.ToString().Substring(startIndex, 3) + numberDelimiter;
                        }
                        else
                        {
                            finalNumber += leftPart.ToString().Substring((int)startIndex, 3);
                        }

                        startIndex = endIndex;
                        endIndex = startIndex + 3;
                    }
                }

                return (rightPart > 0) ? finalNumber + delimiter + rightPart.ToString() : finalNumber;
            }
            catch (Exception)
            {
                return inputValue;
            }
        }

