        public static string CapitalizeFirstLetter(string input)
        {
            string str = "";
            string[] inputs = input.Split(' ');
            foreach (string word in inputs)
            {
                if (word != "")
                    str += char.ToUpper(word[0]) + word.Substring(1) + " ";
            }

            inputs = input.Split('\'');
            if (inputs.Length > 1)
            {
                str = "";
                foreach (string word in inputs)
                {
                    if (word != "")
                        str += char.ToUpper(word[0]) + word.Substring(1) + " ";
                }
            }

            inputs = input.Split('_');
            if (inputs.Length > 1)
            {
                str = "";
                foreach (string word in inputs)
                {
                    if (word != "")
                        str += char.ToUpper(word[0]) + word.Substring(1) + " ";
                }
            }

            return str.TrimEnd();
        }

