        public static string ImportEmailsFromCSV(HtmlInputFile inputFile, string filePath, DBSession session, out string error)
        {
            string emails = "";
            error = "";

            if (inputFile != null && inputFile.PostedFile.ContentLength > 0)
            {
                List<string> values = inputFile.Value.Split('.').ToList();
                if (values.Count > 0)
                {
                    string extension = values[values.Count - 1];
                    filePath += extension.ToUpper().Trim() + "\\";

                    var fileContent = inputFile.PostedFile;
                    string fileName = fileContent.FileName;

                    bool successUpload = UpLoadImage.UpLoadFile(filePath, fileContent, out fileName, session);

                    if (successUpload)
                    {
                        //Upload and save the file  
                        string csvPath = filePath + fileName;
                        //FileUpload1.SaveAs(csvPath);

                        //Create a DataTable.  
                        //DataTable table = new DataTable();
                        //table.Columns.AddRange(new DataColumn[1] { new DataColumn("Email", typeof(string)) });

                        //Read the contents of CSV file.  
                        string csvData = File.ReadAllText(csvPath);

                        List<string> rowsList = csvData.Split('\n').ToList();
                        int rowsCount = (rowsList != null) ? rowsList.Count : 0;

                        if (rowsCount > 0)
                        {
                            foreach (string row in rowsList)
                            {
                                string emailInRow = row.ToString();

                                if (emailInRow.EndsWith("\r") || emailInRow.EndsWith("\n") || emailInRow.StartsWith(@"\") || emailInRow.StartsWith(@"\""") || emailInRow.EndsWith(@"\"""))
                                {
                                    emailInRow = emailInRow.Replace("\r", "").Replace("\n", "").Replace(@"\""", "").Replace("\"", "").Trim();
                                }

                                List<string> columnsList = emailInRow.Split(',').ToList();
                                int columnsCount = (columnsList != null) ? columnsList.Count : 0;

                                if (columnsCount > 0)
                                {
                                    foreach (string cl in columnsList)
                                    {
                                        if (cl != null && cl != "")
                                        {
                                            bool isValidEmail = Validations.IsEmail(cl);
                                            if (isValidEmail)
                                            {
                                                //table.Rows.Add(cl);
                                                emails += cl.ToString() + ",";
                                            }
                                        }
                                    }
                                }
                                else
                                    error = "No emails found in this file";
                            }

                            if (emails != "" && emails.EndsWith(","))
                                emails = emails.Substring(0, emails.Length - 1);
                        }
                        else
                            error = "No rows found in this file";
                    }
                    else
                        error = "File could not be uploaded and saved";
                }
                else
                    error = "Wrong file path";

                if (emails == "")
                {
                    error = "No valid email format could be found in this file";
                    UpLoadImage.DeleteFileFromDirectory(filePath, inputFile.PostedFile.FileName);
                }
            }
            else
                error = "Please select file to be read";

            return emails;
        }

