        public static byte[] ConvertFileToBinary(string filename)
        {
            byte[] bytes;
            using (FileStream file = new FileStream(filename, FileMode.Open, FileAccess.Read))
            {
                bytes = new byte[file.Length];
                file.Read(bytes, 0, (int)file.Length);
            }
            return bytes;
        }

        public static List<string> GetSelectedCategories(List<CheckBoxList> cbxList, out string strItems)
        {
            List<string> selectedItems = new List<string>();
            strItems = string.Empty;

            foreach (CheckBoxList list in cbxList)
            {
                foreach (ListItem item in list.Items)
                {
                    if (item.Selected)
                    {
                        strItems += "'" + item.Text + "', ";
                        selectedItems.Add(item.Text);
                    }
                }
            }

            strItems = (selectedItems.Count > 0 && strItems.Length > 1) ? strItems.Substring(0, strItems.Length - 2) : string.Empty;
            return selectedItems;
        }

