        public static string FixDate(string strDate)
        {
            string[] date = strDate.Split('/');

            if (date.Length >= 3)
            {
                int probablyMonth = Convert.ToInt32(date[0]);
                int probablyDay = Convert.ToInt32(date[1]);

                if (probablyMonth > 12)
                {
                    probablyMonth = Convert.ToInt32(date[1]);
                    probablyDay = Convert.ToInt32(date[0]);
                }

                return probablyDay + " " + GetMonth(probablyMonth) + ", " + date[2];
            }
            else
                return strDate;
        }

        public static string FixDateWithoutHour(string strDate)
        {
            string[] date = new string[3];

            if (strDate.Contains('/'))
                date = strDate.Split('/');
            else if (strDate.Contains('-'))
                date = strDate.Split('-');

            int probablyDay = Convert.ToInt32(date[0]);
            int probablyMonth = Convert.ToInt32(date[1]);
            
            if (probablyMonth > 12)
            {
                probablyMonth = Convert.ToInt32(date[0]);
                probablyDay = Convert.ToInt32(date[1]);
            }

            return probablyDay + " " + GetMonth(probablyMonth) + ", " + date[2];
        }

        public static string FixStringEntryToParagraphs(string entryString)
        {
            string stringToParagraphs = string.Empty;

            if (!string.IsNullOrEmpty(entryString))
            {
                string[] separators = { "\n\n", "\r\r", "\r\n\r\n", "\n" };
                string[] paragraphs = entryString.Split(separators, StringSplitOptions.RemoveEmptyEntries);

                for (int i = 0; i < paragraphs.Length; i++)
                {
                    stringToParagraphs += (i < paragraphs.Length - 1) ? paragraphs[i] + "\r\n\r\n" : paragraphs[i];
                }
            }

            return stringToParagraphs;
        }

        public static string FixParagraphsView(string entryString, int brCount)
        {
            string stringToParagraphs = string.Empty;

            string[] separators = { "\n\n", "\r\r", "\r\n\r\n", "\n" };

            string paragraphsCountBR = "";

            if (brCount == 1)
                paragraphsCountBR = "<br/>";
            else if (brCount == 2)
                paragraphsCountBR = "<br/><br/>";
            else
                paragraphsCountBR = "";

            string[] paragraphs = entryString.Split(separators, StringSplitOptions.RemoveEmptyEntries);
            for (int i = 0; i < paragraphs.Length; i++)
            {
                stringToParagraphs += (i < paragraphs.Length - 1) ? paragraphs[i] + paragraphsCountBR : paragraphs[i];
            }

            return stringToParagraphs;
        }

