        public static bool HasSelectedItemInCheckBoxList(List<CheckBoxList> cbxList)
        {
            bool hasSelectedItem = false;

            foreach (CheckBoxList list in cbxList)
            {
                foreach (ListItem item in list.Items)
                {
                    if (item.Selected)
                    {
                        hasSelectedItem = true;
                        break;
                    }
                }
                if (hasSelectedItem)
                {
                    break;
                }
            }
            return hasSelectedItem;
        }

        public static bool IsValidChecked(List<CheckBoxList> cbxList)
        {
            bool isValid = true;
            int count = 0;

            foreach (CheckBoxList list in cbxList)
            {
                foreach (ListItem item in list.Items)
                {
                    if (item.Selected)
                    {
                        count++;
                    }
                    if (count > 15)
                    {
                        isValid = false;
                        break;
                    }
                }

                if (!isValid)
                {
                    break;
                }
            }

            return isValid;
        }

