        public static ElioUsers InsertSimpleRegistration(string username, string password, string ip, string email, string linkedin, string twitter, DBSession session)
        {
            DataLoader<ElioUsers> loader = new DataLoader<ElioUsers>(session);
            ElioUsers user = new ElioUsers();

            try
            {
                session.BeginTransaction();

                user.Username = username;
                user.UsernameEncrypted = MD5.Encrypt(username);
                user.Password = password;
                user.PasswordEncrypted = MD5.Encrypt(password);
                user.SysDate = DateTime.Now;
                user.LastUpdated = DateTime.Now;
                user.Ip = ip;
                user.Email = email;
                user.GuId = Guid.NewGuid().ToString();
                user.IsPublic = 1;
                user.LinkedInUrl = linkedin;
                user.TwitterUrl = twitter;

                loader.Insert(user);

                session.CommitTransaction();

                return user;
            }
            catch (Exception)
            {
                session.RollBackTransaction();
                return null;
            }
        }

        public static ElioUsers InsertFullRegistration(string officialEmail, string companyType, string phone, string address, string country, string webSite,
                                                       string companyName, string overView, string description, string mashape,
                                                       string lastName, string firstName, string position, string summary,
                                                       string linkedin, string twitter, DBSession session)
        {
            DataLoader<ElioUsers> loader = new DataLoader<ElioUsers>(session);
            ElioUsers user = new ElioUsers();

            try
            {
                session.BeginTransaction();

                #region Personal Info

                user.CompanyType = companyType;
                user.OfficialEmail = officialEmail;
                user.Phone = phone;
                user.Address = address;
                user.Country = country;
                user.WebSite = webSite;
                user.CompanyName = companyName;
                user.Overview = overView;
                user.Description = description;
                user.MashapeUsername = mashape;

                #endregion

                #region Personal Community Info

                user.LastName = lastName;
                user.FirstName = firstName;
                user.Position = position;
                user.CommunitySummaryText = summary;
                user.CommunityStatus = (!string.IsNullOrEmpty(lastName)) ? Convert.ToInt32(AccountStatus.Completed) : Convert.ToInt32(AccountStatus.NotCompleted);
                user.CommunityProfileCreated = DateTime.Now;
                user.CommunityProfileLastUpdated = DateTime.Now;

                user.AccountStatus = Convert.ToInt32(AccountStatus.Completed);

                loader.Update(user);

                #endregion

                session.CommitTransaction();

                return user;
            }
            catch (Exception)
            {
                session.RollBackTransaction();

                return null;
            }
        }

        public static DateTime ReturnValidDateOfMonth(int year, int month, int day)
        {
            int lastDayOfMonth = DateTime.DaysInMonth(year, month);
            day = (lastDayOfMonth < day) ? lastDayOfMonth : day;
            return Convert.ToDateTime(year + "-" + month + "-" + day);
        }

        public static double ConvertSize(double bytes, string type)
        {
            try
            {
                const int CONVERSION_VALUE = 1024;
                //determine what conversion they want
                switch (type)
                {
                    case "BY":
                        //convert to bytes (default)
                        return bytes;
                    case "KB":
                        //convert to kilobytes
                        return (bytes / CONVERSION_VALUE);
                    case "MB":
                        //convert to megabytes
                        return (bytes / CalculateSquare(CONVERSION_VALUE));
                    case "GB":
                        //convert to gigabytes
                        return (bytes / CalculateCube(CONVERSION_VALUE));
                    default:
                        //default
                        return bytes;
                }
            }
            catch (Exception ex)
            {
                Console.WriteLine(ex.Message);
                return 0;
            }
        }

        public static double CalculateSquare(Int32 number)
        {
            return Math.Pow(number, 2);
        }

        public static double CalculateCube(Int32 number)
        {
            return Math.Pow(number, 3);
        }
    }
